insert into message_template(id, notification_type, message_type, language, template ,date_created ) values (1,1,'TEXT',1,'Tetanus is a disorder that leads to severe and painful muscle spasms. It is caused by a bacterium that is commonly found in soil, dust and animal waste.','2009-10-06');
insert into message_template(id, notification_type, message_type, language, template ,date_created ) values (2,2,'TEXT',1,'Immunization against tetanus is recommended especially in pregnant women. Vaccination of pregnant women is the best preventive measure against neonatal tetanus.','2009-10-06');
insert into message_template(id, notification_type, message_type, language, template ,date_created ) values (3,3,'TEXT',1,'You are due for your first tetanus vaccination in your pregnancy.  Please go to your nearest clinic to receive your vaccination.','2009-10-06');
insert into message_template(id, notification_type, message_type, language, template ,date_created ) values (4,4,'TEXT',1,'All wounds should be considered risky, so always be cleaned with soap and water.  Some wounds, however small, such as from thorns of roses or lemon, are highly risky and perhaps even more than other large open wounds.','2009-10-06');
insert into message_template(id, notification_type, message_type, language, template ,date_created ) values (5,5,'TEXT',1,'Tetanus in newborns is severe, with high mortality rate. It usually occurs by contamination of the umbilical cord at delivery or in the first days of life. Furthermore, this disease often aggravated by complications, including infectious, which are the triggers of death.','2009-10-06');
